import { GET_ANIME_START } from '../action_constant';


const initialState = {
    loading: false,
    animeList: {},
    animePagination: {},
};

export default function animeReducer(state = initialState, action) {
    switch (action.type) {
        case GET_ANIME_START:
            return { ...state, loading: true };
        default:
            return state;
    }
}
