import React, { useEffect, useState, useRef, useCallback } from 'react';
import './App.css';
import { useSelector, useDispatch } from 'react-redux';
import { fetchAnimeList } from './actions/animeAction';
import Paper from '@mui/material/Paper';
import InputBase from '@mui/material/InputBase';
import IconButton from '@mui/material/IconButton';
import SearchIcon from '@mui/icons-material/Search';
import Skeleton from '@mui/material/Skeleton';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import _ from 'lodash';

const { debounce } = _;

function App() {
  const anime = useSelector((state) => state.anime);
  const dispatch = useDispatch();
  const [state, setState] = useState({
    searchInput: null,
  });

  const debounced = useRef(debounce((value) => setOutput(value), 600))

  useEffect(() => {
    dispatch(fetchAnimeList());
  }, []);

  const updateUserValue = useCallback(({ target: { value } }) => {
    setState((prevState) => ({ ...prevState, searchInput: value }))
    debounced.current(value)
  }, []);

  const setOutput = (value) => {
    if (value) {
      console.log('value', value);
    }
  }

  console.log('anime', anime);
  return (
    <div className="App">
      <div className="container">
        <div className="row">
          <div className="col-12 search-bar mt-5">
            <Paper
              component="form"
              sx={{ p: '2px 4px', display: 'flex', alignItems: 'center', width: '100vh' }}
              onSubmit={(e) => {
                e.preventDefault();
              }}
            >
              <InputBase
                sx={{ ml: 1, flex: 1 }}
                placeholder="Search"
                onChange={updateUserValue}
              />
              <IconButton type="button" sx={{ p: '10px' }} aria-label="search">
                <SearchIcon />
              </IconButton>
            </Paper>
          </div>
          <div className="col-12 mt-5">
            {anime?.loading ?
              <>
                <Skeleton animation="wave" />
                <Skeleton animation="wave" />
                <Skeleton animation="wave" />
                <Skeleton animation="wave" />
              </> :
              <div className="row">
                <div className="col-3 mb-3">
                  <Card sx={{ maxWidth: '100vh' }}>
                    <CardMedia
                      component="img"
                      alt="green iguana"
                      height="250"
                      image="https://people.com/thmb/pDnF9xhhJ1ZLDBGoZaJHwV1Wbfo=/4000x0/filters:no_upscale():max_bytes(150000):strip_icc():focal(299x0:301x2)/rick-astley-600-d31de1cb3fb248e6b714e5fa789b104d.jpg"
                    />
                    <CardContent>
                      <Typography gutterBottom variant="h5" component="div">
                        Lizard
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                        Lizards are a widespread group of squamate reptiles, with over 6,000
                        species, ranging across all continents except Antarctica
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <Button size="small">Share</Button>
                      <Button size="small">Learn More</Button>
                    </CardActions>
                  </Card>
                </div>
              </div>
            }
          </div>
        </div>
      </div>
    </div >
  );
}

export default App;
