import React, { useEffect, useState, useRef, useCallback } from 'react';
import './App.css';
import { useSelector, useDispatch } from 'react-redux';
import { fetchAnimeDetails } from './actions/animeAction';
import _ from 'lodash';
import { useParams } from 'react-router-dom';
import Skeleton from '@mui/material/Skeleton';
import { ToastContainer } from 'react-toastify';
import {
  Container,
  Grid,
  Card,
  CardMedia,
  Typography,
  Button,
  Box,
  Chip,
  Paper,
  Divider,
  Rating,
  Stack
} from '@mui/material';
import { PlayArrow, Star } from '@mui/icons-material';

function Details() {
  const { id } = useParams();
  const anime = useSelector((state) => state.anime);
  const dispatch = useDispatch();

  const [state, setState] = useState({
    loading: true,
    details: {},
  });

  useEffect(() => {
    dispatch(fetchAnimeDetails(id));
  }, []);

  useEffect(() => {
    setState((prevState) => ({
      ...prevState,
      loading: anime?.loading ?? {},
      details: anime?.animeDetails ?? {},
    }));
  }, [anime]);
  console.log('state', state);
  return (
    <div className="Details">
      <div></div>
      <ToastContainer />
      <div className="container">
        {state?.loading ?
          <>
            <Skeleton animation="wave" />
            <Skeleton animation="wave" />
            <Skeleton animation="wave" />
            <Skeleton animation="wave" />
          </>
          :
          <>
            {/* Left Column - Image and Watch Button */}
            <Grid item xs={12} md={4}>
              <Card elevation={3}>
                <CardMedia
                  component="img"
                  image={state.details?.images.jpg.large_image_url}
                  alt={state.details?.title}
                  sx={{ width: '100%', height: 'auto' }}
                />
              </Card>

              <Box mt={3}>
                <Button
                  variant="contained"
                  color="primary"
                  size="large"
                  fullWidth
                  onClick={() => window.open(state.details?.url, '_blank')}
                  startIcon={<PlayArrow />}
                  sx={{ py: 1.5 }}
                >
                  Watch Now
                </Button>
              </Box>

              {/* Embed trailer directly */}
              <Box mt={4}>
                <Typography variant="subtitle1" fontWeight="bold" gutterBottom>
                  Trailer
                </Typography>
                <Paper elevation={3} sx={{ borderRadius: 2, overflow: 'hidden' }}>
                  <Box sx={{ position: 'relative', paddingTop: '56.25%' }}>
                    <iframe
                      src={state.details?.trailer.embed_url}
                      style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                        border: 'none'
                      }}
                      title="Anime Trailer"
                      allowFullScreen
                    />
                  </Box>
                </Paper>
              </Box>
            </Grid>

            {/* Right Column - Details */}
            <Grid item xs={12} md={8}>
              <Box mb={3}>
                <Typography variant="h3" fontWeight="bold">
                  {state.details?.title_english}
                </Typography>
                <Typography variant="h5" color="text.secondary" gutterBottom>
                  {state.details?.title_japanese}
                </Typography>

                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                  <Star sx={{ color: 'gold' }} />
                  <Typography variant="h6" fontWeight="bold">
                    {state.details?.score}
                  </Typography>
                  <Rating
                    value={state.details?.score / 2}
                    precision={0.25}
                    readOnly
                    sx={{ ml: 1 }}
                  />
                </Box>

                <Grid container spacing={2} mb={3}>
                  <Grid item xs={4}>
                    <Paper elevation={1} sx={{ p: 2, textAlign: 'center' }}>
                      <Typography variant="body2" color="text.secondary">Rank</Typography>
                      <Typography variant="h6" fontWeight="bold">#{state.details?.rank}</Typography>
                    </Paper>
                  </Grid>
                  <Grid item xs={4}>
                    <Paper elevation={1} sx={{ p: 2, textAlign: 'center' }}>
                      <Typography variant="body2" color="text.secondary">Popularity</Typography>
                      <Typography variant="h6" fontWeight="bold">#{state.details?.popularity}</Typography>
                    </Paper>
                  </Grid>
                  <Grid item xs={4}>
                    <Paper elevation={1} sx={{ p: 2, textAlign: 'center' }}>
                      <Typography variant="body2" color="text.secondary">Type</Typography>
                      <Typography variant="h6" fontWeight="bold">{state.details?.type}</Typography>
                    </Paper>
                  </Grid>
                </Grid>

                <Box mb={3}>
                  <Typography variant="subtitle1" fontWeight="bold" mb={1}>
                    Genres
                  </Typography>
                  <Stack direction="row" spacing={1} flexWrap="wrap" gap={1}>
                    {state.details?.genres.map((genre, index) => (
                      <Chip
                        key={index}
                        label={genre.name}
                        color="primary"
                        variant="outlined"
                      />
                    ))}
                    {state.details?.themes.map((theme, index) => (
                      <Chip
                        key={index}
                        label={theme.name}
                        color="secondary"
                        variant="outlined"
                      />
                    ))}
                  </Stack>
                </Box>

                <Box mb={3}>
                  <Typography variant="subtitle1" fontWeight="bold" mb={1}>
                    Rating
                  </Typography>
                  <Chip
                    label={state.details?.rating}
                    color="error"
                    variant="filled"
                  />
                </Box>

                <Divider sx={{ mb: 3 }} />

                <Typography variant="subtitle1" fontWeight="bold" mb={1}>
                  Synopsis
                </Typography>
                <Typography variant="body1" paragraph style={{ whiteSpace: 'pre-line' }}>
                  {state.details?.synopsis}
                </Typography>
              </Box>
            </Grid>
          </>
        }
      </div>
    </div>
  );
}

export default Details;
