import { GET_ANIME_START, GET_ANIME_SUCESS, GET_ANIME_FAIL } from '../action_constant';
import axios from 'axios';

const axiosInstance = axios.create({
    baseURL: 'https://api.jikan.moe/v4',
    headers: {
        'Content-Type': 'application/json',
    },
});

export const fetchAnimeList = (data = null) => (dispatch) => {
    dispatch({ type: GET_ANIME_START });
    axiosInstance.get('/anime')
        .then((res) => {
            dispatch({ type: GET_ANIME_SUCESS, data: res?.data?.data, pagination: res?.data?.pagination });
        })
        .catch((err) => {
            console.log('err', err);
            dispatch({ type: GET_ANIME_FAIL });
        })
}
