import { GET_ANIME_START, GET_ANIME_SUCESS, GET_ANIME_FAIL } from '../action_constant';


const initialState = {
    loading: false,
    animeList: {},
    animePagination: {},
};

export default function animeReducer(state = initialState, action) {
    switch (action.type) {
        case GET_ANIME_START:
            return { ...state, loading: true };
        case GET_ANIME_SUCESS:
            return { ...state, animeList: action.data, animePagination: action.pagination };
        case GET_ANIME_FAIL:
            return { ...state, loading: true };
        default:
            return state;
    }
}
