import { CHANGE_ANIME_LOADING } from '../action_constant';


const initialState = {
    loading: false,
};

export default function animeReducer(state = initialState, action) {
    switch (action.type) {
        case CHANGE_ANIME_LOADING:
            return { ...state, loading: action.loading };
        default:
            return state;
    }
}
